installScript:
#!/bin/bash
set -e

echo '[Aero] Starting Minecraft Paper server installation...'

# Create server directory
mkdir -p {{SERVER_DIR}}
cd {{SERVER_DIR}}

# Download Paper server jar (1.20.4 build 499)
echo '[Aero] Downloading Paper 1.20.4...'
if command -v wget >/dev/null 2>&1; then
  wget -q -O paper.jar https://api.papermc.io/v2/projects/paper/versions/1.20.4/builds/499/downloads/paper-1.20.4-499.jar
elif command -v curl >/dev/null 2>&1; then
  curl -sL -o paper.jar https://api.papermc.io/v2/projects/paper/versions/1.20.4/builds/499/downloads/paper-1.20.4-499.jar
else
  echo '[Aero] ERROR: Neither wget nor curl found!'
  exit 1
fi

# Verify download
if [ ! -f paper.jar ]; then
  echo '[Aero] ERROR: Failed to download paper.jar'
  exit 1
fi

echo '[Aero] Downloaded paper.jar successfully'

# Accept EULA
echo '[Aero] Accepting Minecraft EULA...'
echo 'eula=true' > eula.txt

# Create server.properties with basic settings
cat > server.properties << 'PROPS'
server-port={{PORT}}
difficulty={{DIFFICULTY}}
gamemode={{GAMEMODE}}
pvp={{PVP}}
level-name={{LEVEL_NAME}}
level-seed={{LEVEL_SEED}}
level-type={{LEVEL_TYPE}}
online-mode={{ONLINE_MODE}}
white-list={{WHITE_LIST}}
max-players={{MAX_PLAYERS}}
motd={{MOTD}}
enable-rcon={{ENABLE_RCON}}
PROPS

echo '[Aero] Server configuration created'
echo '[Aero] Installation complete!'
